package app.clients;import app.model.responses.PostResponse;import app.model.responses.UserResponse;import io.reactivex.rxjava3.core.Single;import jakarta.inject.Inject;import jakarta.inject.Singleton;import java.util.Arrays;import java.util.List;@Singletonpublic class UserClient {    @Inject    RestClient restClient;    public Single<List<UserResponse>> GetUsers() {        String apiUrl = "https://gorest.co.in/public/v2/users";        return this.restClient.Get(apiUrl, UserResponse[].class)            .map(response -> Arrays.stream(response).toList());    }    public Single<List<PostResponse>> GetPostByUserId(long userId) {        String apiUrl = "https://gorest.co.in/public/v2/users/%d/posts".formatted(userId);        return this.restClient.Get(apiUrl, PostResponse[].class)            .map(response -> Arrays.stream(response).toList());    }}