package app.controllers;import app.model.UserDto;import app.services.UserService;import io.jooby.annotation.GET;import io.jooby.annotation.Path;import io.reactivex.rxjava3.core.Single;import jakarta.inject.Inject;import java.util.List;public class UserController {    @Inject    UserService userService;    @Path("/users")    @GET    public Single<List<UserDto>> GetUsers() {        return this.userService.GetUsers();    }}