package app.extensions;import app.modules.AppModule;import com.google.inject.Guice;import com.google.inject.Injector;import io.jooby.Extension;import io.jooby.Jooby;import io.jooby.OpenAPIModule;import io.jooby.guice.GuiceModule;import io.jooby.jackson.JacksonModule;import io.jooby.netty.NettyServer;import io.jooby.rxjava3.Reactivex;import org.jetbrains.annotations.NotNull;public class Api implements Extension {    private final Injector injector;    public Api() {        this.injector = Guice.createInjector(new AppModule());    }    @Override    public boolean lateinit() {        return Extension.super.lateinit();    }    @Override    public void install(@NotNull Jooby application) {        application.use(Reactivex.rx());        application.install(this.injector.getInstance(GuiceModule.class));        application.install(this.injector.getInstance(JacksonModule.class));        application.install(this.injector.getInstance(NettyServer.class));        application.install(this.injector.getInstance(OpenAPIModule.class));    }}