package app.providers;import com.fasterxml.jackson.databind.ObjectMapper;import com.google.inject.Provider;import io.jooby.jackson.JacksonModule;import jakarta.inject.Inject;public class JacksonModuleProvider implements Provider<JacksonModule> {    @Inject    ObjectMapper objectMapper;    @Override    public JacksonModule get() {        return new JacksonModule(this.objectMapper);    }}