package app.services;import app.clients.UserClient;import app.model.UserDto;import app.model.responses.UserResponse;import io.reactivex.rxjava3.core.Single;import jakarta.inject.Inject;import jakarta.inject.Singleton;import java.util.ArrayList;import java.util.List;@Singletonpublic class UserService {    @Inject    UserClient userClient;    public Single<List<UserDto>> GetUsers() {        return this.userClient.GetUsers().map(response -> {            List<UserDto> result = new ArrayList<>();            for (UserResponse userResponse : response) {                UserDto userDto = new UserDto();                userDto.Id = userResponse.Id;                userDto.Name = userResponse.Name;                result.add(userDto);            }            return result;        });    }}