package com.github;import io.jooby.Router;import io.jooby.internal.netty.NettyContext;import io.netty.channel.ChannelHandlerContext;import io.netty.handler.codec.http.HttpRequest;public class ApiContext extends NettyContext {	public ApiContext(ChannelHandlerContext ctx,		HttpRequest req, Router router, String path,		int bufferSize, boolean http2) {		super(ctx, req, router, path, bufferSize, http2);	}}