package com.github;import com.github.sdk.http.RestClient;import com.github.sdk.modules.InjectionModule;import com.google.inject.name.Names;import java.util.Arrays;public class ApplicationModule extends InjectionModule {	@Override	protected void configure() {		super.configure();		for (String clientName : Arrays.asList("users-client", "posts-client", "todos-client")) {			bind(RestClient.class).annotatedWith(Names.named(clientName))				.toInstance(new RestClient("https://gorest.co.in/public/v2"));		}	}}