package com.github;import com.github.sdk.http.RestClient;import com.github.sdk.modules.InjectionModule;import com.google.inject.name.Names;public class ApplicationModule extends InjectionModule {	@Override	protected void configure() {		super.configure();		bind(RestClient.class).annotatedWith(Names.named("users-client"))			.toInstance(new RestClient("https://gorest.co.in/public/v2"));		bind(RestClient.class).annotatedWith(Names.named("posts-client"))			.toInstance(new RestClient("https://gorest.co.in/public/v2"));		bind(RestClient.class).annotatedWith(Names.named("todos-client"))			.toInstance(new RestClient("https://gorest.co.in/public/v2"));	}}