package com.github.core;import com.github.ApiContext;import io.jooby.Context;import java.util.function.BiFunction;public class ApiRoute<TController, TResult> {	public String verb;	public String path;	public Class<TController> type;	public BiFunction<Context, ?, ?> action;	public ApiRoute(String verb, String path, Class<TController> type,		BiFunction<Context, TController, TResult> action) {		this.verb = verb;		this.path = path;		this.type = type;		this.action = action;	}}