package com.github.sdk;import io.jooby.Context;import java.util.function.BiFunction;public class ApiRoute<TController, TResult> {	public final String verb;	public final String path;	public final Class<TController> type;	public final BiFunction<Context, ?, ?> action;	public ApiRoute(String verb, String path, Class<TController> type,		BiFunction<Context, TController, TResult> action) {		this.verb = verb;		this.path = path;		this.type = type;		this.action = action;	}}